import { APP_ACTIONS } from "./actions";

const defaultState = {
  formType: null,
  signInForm: {
    email: "",
    password: "",
  },
  signUpForm: {
    email: "",
    password: "",
  },
};

const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case APP_ACTIONS.showSignInForm:
      return {
        ...state,
        formType: "sign-in",
        signInForm: {
          email: "",
          password: "",
        },
      };

    case APP_ACTIONS.showSignUpForm:
      return {
        ...state,
        formType: "sign-up",
        signUpForm: {
          email: "",
          password: "",
        },
      };

    case APP_ACTIONS.onSignInEmailChange:
      return {
        ...state,
        signInForm: {
          ...state.signInForm,
          email: action.payload,
        },
      };

    case APP_ACTIONS.onSignInPasswordChange:
      return {
        ...state,
        signInForm: {
          ...state.signInForm,
          password: action.payload,
        },
      };

    default:
      return state;
  }
};

export default reducer;
