import React from "react";
import { connect } from "react-redux";

import {
  onSignInEmailChange,
  onSignInPasswordChange,
} from "../../../redux/actions";

import "./SignInForm.css";

const SignInForm = (props) => {
  const { forSignIn, appReduxStoreState } = props;

  return (
    <div className="SignInForm">
      <div>{forSignIn ? "Sign in to your account" : "Sign up an account"}</div>

      <div className="form-input">
        Email:
        <input
          type="email"
          value={appReduxStoreState.signInForm.email}
          onChange={(e) => props.onSignInEmailChange(e)}
        />
      </div>

      <div className="form-input">
        Password:
        <input type="password" />
      </div>

      <div>
        <button>{forSignIn ? "Sign in" : "Create an account"}</button>
      </div>

      <div>Forget Password</div>
    </div>
  );
};

let mapStateToProps = (state) => {
  return {
    appReduxStoreState: state,
  };
};

let mapDispatchToProps = { onSignInEmailChange, onSignInPasswordChange };

export default connect(mapStateToProps, mapDispatchToProps)(SignInForm);
